document.getElementById("contact-form").addEventListener("submit",async function(o){o.preventDefault();let e=o.target,a=new FormData(e),n=Object.fromEntries(a.entries());try{let t=await fetch("/contact?deck=1",{method:e.method,headers:{"Content-Type":"application/json",Accept:"application/json"},body:JSON.stringify(n)});if(t.ok){let r=await t.json();alert(r.success),e.reset()}else{let r=await t.json();alert(r.error||"An error occurred. Please try again.")}}catch(t){alert("Network error: "+t.message)}});
